mapper.insert:
INSERT
INTO t_board(title, text, date, hits)
VALUES(#{title}, #{text}, CONVERT(VARCHAR, GETDATE(), 120), 0);

mapper.select:
SELECT
* FROM t_board
ORDER BY no DESC;

SELECT
* FROM t_board
WHERE title LIKE '%#{keyword}%';

SELECT
* FROM t_board
ORDER BY no DESC;

mapper.update:
UPDATE
t_board SET title=#{title}, text=#{text}
WHERE no = #{no}

UPDATE
t_board SET hits=#{hits} 
WHERE no=#{no}
